import logo from './logo.svg';
import './App.css';
import BarChart from './components/BarChart';
import * as d3 from 'd3';
import React from 'react';
import ScatterPlot from './components/ScatterPlot';
import { filter } from 'd3';


function App() {

    var clusterMaker = require('clusters');

    var [data, setData] = React.useState([]);
    var [selectedData, setSelectedData] = React.useState(data);
    var [filterCategory, setFilterCategory] = React.useState(['Easy', 'Intermediate', 'Difficult']);


    const colorScale = d3.scaleOrdinal()
        .range(['#d3eecd', '#7bc77e', '#2a8d46']) 
        .domain(['Easy','Intermediate','Difficult']);

    React.useEffect(()=>{
        loadData();

        console.log(data)

        clusterMaker.k(3);
        clusterMaker.iterations(1000);
        clusterMaker.data(data);
        console.log(clusterMaker.clusters());
    },[])

    const loadData = () => {
        // data adapted from CSC3020
        d3.csv('./generatedData.csv') 
        .then(_data => {
            setData(_data.map(d => {
                d.Width = +d.Width;
                d.Length = +d.Length;   
                return [d.Length, d.Width]
            }));
        })
    }


    // Use useEffect to render and update visual results when dependency/dependencies change (30pts)

    // To DO

    React.useEffect(() => {
        updateData();
    }, [data])

    const updateData = () => {
        if (data.filter(d => filterCategory.includes(d.difficulty)) != selectedData){
            setSelectedData(data.filter(d => filterCategory.includes(d.difficulty)))
        }
    }

    return (
        <div className='Container'>
        <h1 className='head'> Multiple-View Interaction </h1>
        <div className="App">
        <ScatterPlot config={colorScale} data = {selectedData}/>
        <BarChart config={colorScale} data = {data} setFilterCategory={setFilterCategory}/>
        </div>
        </div>
    );
}

export default App;
