import logo from './logo.svg';
import './App.css';
import BarChart from './components/BarChart';
import * as d3 from 'd3';
import React, { useEffect } from 'react';
import ScatterPlot from './components/ScatterPlot';
import { cluster, filter } from 'd3';


function App() {

    var clusterMaker = require('clusters');

    function useFirstRender() {
        const firstRender= React.useRef(true);

        useEffect(() => {
            firstRender.current = false;
        }, []);

        return firstRender.current;
    } 

    const firstRender = useFirstRender();

    var [tmpdata, settmpData] = React.useState([]);
    var [data, setData] = React.useState([]);
    var [selectedData, setSelectedData] = React.useState(data);
    var clusters = [];
    var processed = [];


    React.useEffect(() => {
        loadData();
        if (!(firstRender || tmpdata == [])) {
            processData();
        }
    },[firstRender])


    const colorScale = d3.scaleOrdinal()
        .range(['#d3eecd', '#7bc77e', '#2a8d46']) 
        .domain([1,'Intermediate','Difficult']);

    const loadData = () => {
        // data adapted from CSC3020
        d3.csv('./generatedData.csv') 
        .then(_data => {
            settmpData(_data.map(d => {
                d.width = +d.width;
                d.length = +d.length;   
                return [d.length, d.width]
            }));
        })
    }

    const processData = () => {
        clusterMaker.k(3);
        clusterMaker.iterations(1000);
        clusterMaker.data(tmpdata)
        clusters = clusterMaker.clusters()
        for (var i=0; i<clusters.length; i++){
            for (var j=0; j<clusters[i].points.length;j++){
                var tmp = {
                    "length": clusters[i].points[j][0],
                    "width": clusters[i].points[j][1],
                    "cluster": i
                }
                processed.push(tmp)
            }
        }
        setData(processed);
    }

    React.useEffect(() => {
        updateData();
    }, [data, selectedData])

    const updateData = () => {
        setSelectedData(data)

    }

    return (
        <div className='Container'>
        <h1 className='head'> CSC4130 Project </h1>
        <div className="App">
        <ScatterPlot config={colorScale} data={data}/>
        </div>
        <div className="input">
            <input type='text' id=""></input>
        </div>
        </div>
    );
}

export default App;
