import logo from './logo.svg';
import './App.css';
import BarChart from './components/BarChart';
import * as d3 from 'd3';
import React from 'react';
import ScatterPlot from './components/ScatterPlot';
import { cluster, filter } from 'd3';


function App() {

    var clusterMaker = require('clusters');
    clusterMaker.k(3);
    clusterMaker.iterations(1000);

    const firstRender = React.useRef(true);

    var [data, setData] = React.useState([]);
    var [clusters, setClusters] = React.useState([]);
    var [selectedData, setSelectedData] = React.useState(data);


    const colorScale = d3.scaleOrdinal()
        .range(['#d3eecd', '#7bc77e', '#2a8d46']) 
        .domain(['Easy','Intermediate','Difficult']);

    const loadData = () => {
        // data adapted from CSC3020
        d3.csv('./generatedData.csv') 
        .then(_data => {
            setData(_data.map(d => {
                d.width = +d.width;
                d.length = +d.length;   
                return [d.length, d.width]
            }));
        })
    }

    React.useEffect(() => {
        loadData();
        firstRender.current = false;
        console.log(data);
        // processData();
        // console.log(clusters)
    },[firstRender])



    const processData = () => {
        clusterMaker.data(data)
        setClusters(clusterMaker.clusters())
    }

    return (
        <div className='Container'>
        <h1 className='head'> CSC4130 Project </h1>
        <div className="App">
        <ScatterPlot config={colorScale} data = {selectedData}/>
        </div>
        </div>
    );
}

export default App;
